void set_refresh_rate(struct video_canvas_s *canvas) {
  if (is_ntsc()) {
    canvas->refreshrate = C128_NTSC_RFSH_PER_SEC;
  } else {
    canvas->refreshrate = C128_PAL_RFSH_PER_SEC;
  }
}

void set_video_font(void) {
  int i;
  video_font = mem_chargen_rom + 0x800;
  raw_video_font = mem_chargen_rom;
  for (i = 0; i < 256; ++i) {
    video_font_translate[i] = 8 * ascii_to_petscii[i];
  }
}

unsigned int *raspi_get_palette(int display, int index) {
  if (display == 0) {
    switch (index) {
    case 0:
      return vice_color_palette;
      break;
    case 1:
      return pepto_pal_color_palette;
      break;
    case 2:
      return pepto_palold_color_palette;
      break;
    case 3:
      return pepto_ntsc_sony_color_palette;
      break;
    case 4:
      return pepto_ntsc_color_palette;
      break;
    case 5:
      return colodore_color_palette;
      break;
    case 6:
      return cjam_color_palette;
      break;
    case 7:
      return c64hq_color_palette;
      break;
    case 8:
      return c64s_color_palette;
      break;
    case 9:
      return ccs64_color_palette;
      break;
    case 10:
      return frodo_color_palette;
      break;
    case 11:
      return godot_color_palette;
      break;
    case 12:
      return pc64_color_palette;
      break;
    case 13:
      return rgb_color_palette;
      break;
    case 14:
      return deekay_color_palette;
      break;
    case 15:
      return ptoing_color_palette;
      break;
    case 16:
      return community_colors_color_palette;
      break;
    default:
      return NULL;
    }
  } else {
    switch (index) {
    case 0:
      return vdc_deft_color_palette;
      break;
    case 1:
      return vdc_comp_color_palette;
      break;
    default:
      return NULL;
    }
  }
}

void set_canvas_size(int index, int *w, int *h, int *gw, int *gh) {
  if (index == VIC_INDEX) {
    *w = 384;
    *h = 272;
    *gw = 40*8;
    *gh = 25*8;
    return;
  }

  *w = 856;
  *h = 312;
  *gw = 80*8;
  *gh = 25*8;
}

void set_canvas_borders(int index, int *w, int *h) {
  if (index == VIC_INDEX) {
    if (is_ntsc()) {
       *w = 32;
       *h = 23;
    } else {
       *w = 32;
       *h = 36;
    }
    return;
  }

  if (is_ntsc()) {
      *w = 112;
      *h = 14;
  } else {
      *w = 112;
      *h = 38;
  }
}

void set_filter(int display, int value) {
  if (display == 0) {
     resources_set_int("VICIIFilter", value);
  } else {
     resources_set_int("VDCFilter", value);
  }
}

int get_filter(int display) {
  int value;
  if (display == 0) {
     resources_get_int("VICIIFilter", &value);
  } else {
     resources_get_int("VDCFilter", &value);
  }
  return value;
}
