void set_refresh_rate(struct video_canvas_s *canvas) {
  if (is_ntsc()) {
    canvas->refreshrate = PLUS4_NTSC_RFSH_PER_SEC;
  } else {
    canvas->refreshrate = PLUS4_PAL_RFSH_PER_SEC;
  }
}

void set_video_font(void) {
  int i;
  video_font = plus4memrom_kernal_rom + 0x1400;
  raw_video_font = plus4memrom_kernal_rom + 0x1000;
  for (i = 0; i < 256; ++i) {
    video_font_translate[i] = 8 * ascii_to_petscii[i];
  }
}

unsigned int *raspi_get_palette(int display, int index) {
  switch (index) {
  case 0:
    return yape_pal_color_palette;
    break;
  case 1:
    return yape_ntsc_color_palette;
    break;
  case 2:
    return colodore_ted_color_palette;
    break;
  default:
    return NULL;
  }
}

void set_canvas_size(int index, int *w, int *h, int *gw, int *gh) {
  *w = 384;
  *h = 288;
  *gw = 40*8;
  *gh = 25*8;
}

void set_canvas_borders(int index, int *w, int *h) {
  if (is_ntsc()) {
      *w = 32;
      *h = 16;
  } else {
      *w = 32;
      *h = 40;
  }
}

void set_filter(int display, int value) {
  resources_set_int("TedFilter", value);
}

int get_filter(int display) {
  int value;
  resources_get_int("TedFilter", &value);
  return value;
}
